function makeData(msgType, format, lastClose) {
    var size = format.title.length;
    var output = "";
    for (var i=0; i<size; i++) {
        switch(format.type[i]) {
            case 'c' :
                if (msgType === "slab") {
                    if (i == 0)
                        output += msgType;
                    else
                        output += getUID(format.len[i]);
                } else if (msgType === "roll") {
                    if (i == 1)
                        output += msgType;
                    else if (i == 0) {
                        var date = new Date();
                        output += date.yyyymmdd();
                    } else
                        output += getUID(format.len[i]);
                }
                break;
            case 'f' :
                output += getRandomTemperator(format.len[i]);
                break;
            case 'b' :
                output += getBooleanData(false);
                break;
            case 'i' :
                output += getRandomTemperator(format.len[i]);
                break;
        }
    }
    if (lastClose === true)
        output += "\n";
    console.log(output);
    console.log(output.length);
    return output;
}
