function checkLengthString(str, len) {
    var check = str.length;
    if (check < len) {
        for (var i = 0 ; i < (len - check) ; i++) {
            str += " ";
        }
    } else if(check > len) {
        str = str.slice(0, len)
    }
    return str;
}

function createDataRefine(value) {
