        var valueStr = context.global.dataMap.get(context.global.SendDataFormat.title[i]);
        returnStr += checkLengthString(valueStr, context.global.SendDataFormat.len[i]);
    }
    if (value == true) {
        returnStr += "\n";
    }
    return returnStr;
}

